import React from 'react';
import ProductItem from './ProductItem';
import SearchBar from './SearchBar'; // Importa tu componente de barra de búsqueda
import './ProductList.css'; // Importa el archivo CSS


const ProductList = ({ products, onAddToCart, onSearch }) => {
  return (
    <div>
      <SearchBar onSearch={onSearch} /> {/* Añade la barra de búsqueda central */}
      <div className="product-list">
        {products.map(product => (
          <ProductItem key={product._id} product={product} onAddToCart={onAddToCart} />
        ))}
      </div>
    </div>
  );
};

export default ProductList;
