// src/components/ReturnOrder.js
import React, { useState } from 'react';
import './ReturnOrder.css';

const ReturnOrder = () => {
  const [orderId, setOrderId] = useState('');
  const [message, setMessage] = useState('');

  const handleReturnSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:5000/api/returns`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ orderId }),
      });

      if (response.ok) {
        setMessage('Devolución solicitada exitosamente');
      } else {
        setMessage('Error al solicitar la devolución');
      }
    } catch (error) {
      setMessage('Error al enviar la solicitud de devolución');
    }
  };

  return (
    <div className="return-container">
      <h2>Devolver Pedido</h2>
      <form onSubmit={handleReturnSubmit} className="return-form">
        <div>
          <label htmlFor="orderId">ID del Pedido:</label>
          <input
            type="text"
            id="orderId"
            value={orderId}
            onChange={(e) => setOrderId(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="return-button">Solicitar Devolución</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ReturnOrder;
