import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import ProductList from './components/ProductList';
import SearchBar from './components/SearchBar';
import Cart from './components/Cart';
import Home from './components/Home'; // Importa el componente Home
import About from './components/About'; // Importa el componente About
import Contact from './components/Contact'; // Importa el componente Contact
import Checkout from './components/Checkout'; // Importa el componente Checkout


const App = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data);
      })
      .catch((error) => console.error('Error al obtener productos:', error));
  }, []);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter((product) =>
        product.name.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredProducts(filtered);
    }
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  return (
    <Router>
      <div>
        <header>
          <h1>Ferretería FerTools</h1>
          <img src="/images/logo.png" alt="Ferretería FerTools Logo" className="logo" />
        </header>
        <nav>
          <Link to="/">Inicio</Link>
          <Link to="/acerca-de">Acerca de</Link>
          <Link to="/contacto">Contacto</Link>
          <Link to="/productos">Productos</Link>
          <Link to="/carrito">Carrito ({cart.length})</Link>
          <Link to="/pagar">Pagar</Link>
        </nav>
        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/acerca-de" element={<About />} />
            <Route path="/contacto" element={<Contact />} />
            <Route path="/productos" element={
              <div>
                <SearchBar onSearch={handleSearch} />
                <ProductList products={filteredProducts} onAddToCart={handleAddToCart} />
              </div>
            } />
            <Route path="/carrito" element={<Cart cart={cart} removeFromCart={removeFromCart} />} />
            <Route path="/pagar" element={<Checkout />} />
          </Routes>
        </main>
        <footer className="footer">
          <p>2024 Ferretería FerTools</p>
        </footer>
      </div>
    </Router>
  );
};

export default App;
