// src/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import ProductList from './components/ProductList';
import SearchBar from './components/SearchBar';
import './App.css';

const App = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data);
      })
      .catch((error) => console.error('Error al obtener productos:', error));
  }, []);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter((product) =>
        product.name.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredProducts(filtered);
    }
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <Router>
      <div>
        <header>
          <h1>Ferretería FerTools</h1>
          <img src="/images/logo.png" alt="Ferretería FerTools Logo" className="logo" />
        </header>
        <nav>
          <Link to="/">Inicio</Link>
          <Link to="/acerca-de">Acerca de</Link>
          <Link to="/contacto">Contacto</Link>
          <Link to="/productos">Productos</Link>
          <Link to="/carrito">Carrito ({cart.length})</Link>
          <Link to="/pagar">Pagar</Link>
        </nav>
        <Routes>
          <Route exact path="/" element={<Inicio />} />
          <Route path="/acerca-de" element={<AcercaDe />} />
          <Route path="/contacto" element={<Contacto />} />
          <Route path="/productos" element={
            <div>
              <SearchBar onSearch={handleSearch} />
              <ProductList products={filteredProducts} onAddToCart={handleAddToCart} />
            </div>
          } />
          <Route path="/carrito" element={<Carrito cart={cart} />} />
          <Route path="/pagar" element={<Pagar />} />
        </Routes>
        <footer className="footer">
          <p>2024 Ferretería FerTools</p>
        </footer>
      </div>
    </Router>
  );
};

const Inicio = () => (
  <div>
    <h2>Bienvenido a Ferretería FerTools</h2>
    <p>Encuentra todas las herramientas y materiales que necesitas para tus proyectos de construcción y bricolaje.</p>
  </div>
);

const AcercaDe = () => (
  <div>
    <h2>Acerca de</h2>
    <p>Ferretería FerTools es tu tienda de confianza para adquirir todo tipo de herramientas y materiales de construcción. Contamos con más de 20 años de experiencia en el mercado.</p>
  </div>
);

const Contacto = () => (
  <div>
    <h2>Contacto</h2>
    <p>Si tienes alguna pregunta, no dudes en contactarnos:</p>
    <ul>
      <li>Email: contacto@fertools.com</li>
      <li>Teléfono: +52 555 123 4567</li>
      <li>Dirección: Calle de las Herramientas 123, Ciudad de México</li>
    </ul>
  </div>
);

const Carrito = ({ cart }) => (
  <div>
    <h2>Carrito</h2>
    {cart.length === 0 ? (
      <p>Tu carrito de compras está vacío.</p>
    ) : (
      <ul>
        {cart.map((item, index) => (
          <li key={index}>{item.name} - ${item.price}</li>
        ))}
      </ul>
    )}
  </div>
);

const Pagar = () => (
  <div>
    <h2>Pagar</h2>
    <p>Proceso de pago seguro y confiable.</p>
  </div>
);

export default App;
