import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import './Cart.css';

const Cart = () => {
  const { cart, removeFromCart } = useContext(CartContext);

  return (
    <div className="cart-container">
      <h2>Mi Carrito</h2>
      {cart.length === 0 ? (
        <p>Tu carrito de compras está vacío.</p>
      ) : (
        <div className="cart-items">
          {cart.map((item, index) => (
            <div className="cart-item" key={index}>
              <div className="cart-item-image">
                <img src={item.image} alt={item.name} />
              </div>
              <div className="cart-item-info">
                <h4>{item.name}</h4>
                <p>${item.price}</p>
                <button className="remove-button" onClick={() => removeFromCart(index)}>Eliminar</button>
              </div>
            </div>
          ))}
        </div>
      )}
      <div className="cart-summary">
        <h3>Resumen del pedido</h3>
        <p>Subtotal: ${cart.reduce((total, item) => total + item.price, 0).toFixed(2)}</p>
        <p>Envío estimado: $5.00</p>
        <h3>Total: ${(cart.reduce((total, item) => total + item.price, 0) + 5).toFixed(2)}</h3>
        <button className="checkout-button">Guardar</button>
      </div>
    </div>
  );
};

export default Cart;
