// src/components/ReturnOrder.js
import React, { useState } from 'react';

const ReturnOrder = () => {
  const [orderId, setOrderId] = useState('');
  const [message, setMessage] = useState('');

  const handleReturnOrder = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}/return`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setMessage('Pedido devuelto exitosamente');
      } else {
        const errorData = await response.json();
        setMessage(`Error: ${errorData.message}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  return (
    <div>
      <h2>Devolver Pedido</h2>
      <input
        type="text"
        value={orderId}
        onChange={(e) => setOrderId(e.target.value)}
        placeholder="ID del Pedido"
      />
      <button onClick={handleReturnOrder}>Devolver Pedido</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ReturnOrder;
