import React from 'react';
import './Cart.css';

const Cart = ({ cart, removeFromCart }) => {
  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price, 0).toFixed(2);
  };

  const shippingCost = 5.00; // Costo de envío fijo

  return (
    <div className="cart-container">
      <div className="cart-items">
        <h2>Mi Carrito</h2>
        {cart.length === 0 ? (
          <p>Tu carrito de compras está vacío.</p>
        ) : (
          <table className="cart-table">
            <thead>
              <tr>
                <th>Producto</th>
                <th>Precio</th>
                <th>Acción</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={index} className="cart-item">
                  <td>{item.name}</td>
                  <td>${item.price.toFixed(2)}</td>
                  <td>
                    <button className="remove-button" onClick={() => removeFromCart(index)}>Eliminar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
      <div className="cart-summary">
        <h3>Resumen del pedido</h3>
        <p>Subtotal: ${calculateTotalPrice()}</p>
        <p>Envío estimado: ${shippingCost.toFixed(2)}</p>
        <h3>Total: ${(parseFloat(calculateTotalPrice()) + shippingCost).toFixed(2)}</h3>
        <button className="checkout-button">Pagar</button>
      </div>
    </div>
  );
};

export default Cart;
