// src/components/Contact.js
import React, { useState } from 'react';
import './Contact.css';

function Contact() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, message }),
      });

      if (response.ok) {
        setPopupMessage('Información de contacto guardada correctamente');
      } else {
        setPopupMessage('Error al guardar la información de contacto');
      }

      setShowPopup(true);
    } catch (error) {
      console.error('Error:', error);
      setPopupMessage('Error al enviar la información');
      setShowPopup(true);
    }
  };

  return (
    <div className="contact">
      <h2 className="contact__title">Contáctanos</h2>
      <p className="contact__text">Nos encantaría saber de ti.</p>
      
      <section className="contact__section">
        <h3 className="contact__section-title">Formulario de Contacto</h3>
        <form className="contact__form" onSubmit={handleSubmit}>
          <div className="contact__form-group">
            <label htmlFor="name" className="contact__label">Nombre:</label>
            <input
              type="text"
              id="name"
              name="name"
              className="contact__input"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="contact__form-group">
            <label htmlFor="email" className="contact__label">Correo Electrónico:</label>
            <input
              type="email"
              id="email"
              name="email"
              className="contact__input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="contact__form-group">
            <label htmlFor="message" className="contact__label">Mensaje:</label>
            <textarea
              id="message"
              name="message"
              className="contact__textarea"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            ></textarea>
          </div>
          <button type="submit" className="contact__button">Enviar</button>
        </form>
      </section>
      
      {showPopup && (
        <div className="popup">
          <p>{popupMessage}</p>
          <button onClick={() => setShowPopup(false)}>Cerrar</button>
        </div>
      )}
    </div>
  );
}

export default Contact;
